import React, { useState } from "react";
import "../Style/googlekeep.css";
const Createnote = (props) => {
  const [note, setNote] = useState({
    title: "",
    content: "",
  });

  const handelkeepchenge = (event) => {

    const { name, value } = event.target;

// if (name !== " " && value !== "") {

    setNote((previous) => {
      return {
        ...previous,
        [name]: value,
      };
    });

  // } else {
  
// alert("Please enter something")
// }

